//表情栏
import EmojiReact from 'react-emoji-react';
import React, { Component } from 'react';
import ChatClient from '../ChatClient';
import '../css/emjoi.css';
const emojis = [
  {
    name: 'rage',
    count: 2
  },
  {
    name: 'blush',
    count: 1
  },
  {
    name: 100,
    count: 3
  },
  {
    name: 'grinning',
    count: 2
  }
];
class Emjoi extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emojis
    };
  }
  //在输入框内添加表情
  setEmjoi(e) {
    this.setState({ cls_icon: ['iconCard'] })
    let input = document.getElementsByClassName('input')[0];
    let str = input.innerText;
    //获取光标位置
    let pos = ChatClient.getCursortPosition(input);
    //将输入框内容截成两段
    let msg_1 = str.substring(0, pos);
    let msg_2 = str.substring(pos);
    //获取点击的表情的name属性
    let faceName = e.target.name;
    input.innerText = [msg_1, msg_2].join(faceName);
    ChatClient.setCaretPosition(input, pos + faceName.length);
  }
  render() {
    let { className } = this.props;
    return (
      <ul className={className}>
        <EmojiReact
          reactions={this.state.emojis}
          onReaction={(name) => this.onReaction(name)}
          onEmojiClick={(name) => this.onEmojiClick(name)}
        />
      </ul>
    )
  }
  onReaction(name) {
    const emojis = this.state.emojis.map(emoji => {
      return emoji;
    });
    this.setState({ emojis });
    this.props.closeEmjoi(false)
  }
  onEmojiClick(name) {
    const emojis = this.state.emojis.concat([{name, count: 1}]);
    this.setState({ emojis });
    
  }
}
export default Emjoi;
