//表情栏
import React, { Component } from 'react';
import ChatClient from '../ChatClient';
import '../css/emjoi.css';
class Emjoi extends Component {
  constructor(props) {
    super(props);
    this.state = {
      emjoi: [
        '../emoji/1.png','../emoji/2.png','../emoji/3.png',
        '../emoji/4.png','../emoji/5.png','../emoji/6.png',
        '../emoji/7.png','../emoji/8.png','../emoji/9.png',
        '../emoji/10.png','../emoji/11.png','../emoji/12.png',
        '../emoji/13.png','../emoji/14.png','../emoji/15.png',
        '../emoji/16.png','../emoji/17.png','../emoji/18.png',
        '../emoji/19.png','../emoji/20.png','../emoji/21.png',
        '../emoji/22.png','../emoji/23.png','../emoji/24.png',
        '../emoji/25.png','../emoji/26.png','../emoji/27.png',
        '../emoji/28.png','../emoji/29.png','../emoji/30.png',
        '../emoji/31.png','../emoji/32.png','../emoji/33.png',
        '../emoji/34.png','../emoji/35.png','../emoji/36.png',
        '../emoji/37.png','../emoji/38.png','../emoji/39.png',
        '../emoji/40.png'
      ],
      Code: [
        '[e:~]','[e:!]','[e:@]','[e:#]','[e:$]',
        '[e:%]','[e:^]','[e:&]','[e:*]','[e:(]',
        '[e:)]','[e:Q]','[e:W]','[e:E]','[e:R]',
        '[e:T]','[e:Y]','[e:U]','[e:I]','[e:O]',
        '[e:P]','[e:A]','[e:S]','[e:D]','[e:F]',
        '[e:G]','[e:H]','[e:J]','[e:K]','[e:L]',
        '[e:Z]','[e:X]','[e:C]','[e:V]','[e:B]',
        '[e:N]','[e:M]','[e:<]','[e:>]','[e:?]'
      ]
    }
  }
  componentDidMount(){
  }
  //在输入框内添加表情
  setEmjoi(e) {
    this.setState({ cls_icon: ['iconCard'] })
    let input = document.getElementsByClassName('input')[0];
    let str = input.innerText;
    //获取光标位置
    let pos = ChatClient.getCursortPosition(input);
    //将输入框内容截成两段
    let msg_1 = str.substring(0, pos);
    let msg_2 = str.substring(pos);
    //获取点击的表情的name属性
    let faceName = e.target.name;
    input.innerText = [msg_1, msg_2].join(faceName);
    ChatClient.setCaretPosition(input, pos + faceName.length);
  }
  render() {
    let { className } = this.props;
    return (
      <ul className={className}>
        <div>
          <span>{ this.emojify(this.props.text) }</span>
          <span>{ ReactEmoji.emojify(this.props.text) }</span> // or can be used no mixin way
        </div>
          {/* <span className='point'></span>
          {
            this.state.Code.map((item, index) => {
              return <li className='emjoi_1' key={index}>
                <img src={this.state.emjoi[index]} className='img_1'
                  onClick={(e) => this.setEmjoi(e)} name={item} alt='' />
              </li>
            })

          } */}
      </ul>
    )
  }
}
export default Emjoi;
